mapper.select:
select count(1) from mc_activity_plan ap where EXISTS ( select 1 from mc_activity_tags tg where tg.activity_plan_id = ap.activity_plan_id
and tg.tag_name in
#{tag}
having count(tg.activity_tags_id) - #{paramCount} = 0
)

select ap.activity_plan_id as activityPlanId, ap.name as name, ap.description as description, ap.departure as departure, ap.destination as destination, ap.price as price, ap.price_desc as priceDesc, ap.take_time as takeTime, ap.traffic as traffic, ap.min_pepole_num as minPepoleNum, ap.max_pepole_num as maxPepoleNum, ap.pre_reg_day as preRegDay, ap.create_time as createTime, ap.sort as sort, ap.image_url as imageURL from mc_activity_plan ap where EXISTS ( select 1 from mc_activity_tags tg where tg.activity_plan_id = ap.activity_plan_id
and tg.tag_name in
#{tag}
having count(tg.activity_tags_id) - #{paramCount} = 0
) limit #{from},#{pageSize}
